@import url("https://fonts.googleapis.com/css2?family=Anton&display=swap");
:root {
  --brand: #4f00cf;
  --font-base: "Lato", sans-serif;
  --font-heading: "Anton", sans-serif;
  --color-bg: #fff;
}

body {
  font-family: var(--font-base);
  font-size: 1rem;
  font-weight: 300;
  line-height: 1.4;
}

.title {
  font-family: var(--font-heading);
  text-transform: uppercase;
}

.title--h1 {
  font-size: 50px;
}

.title--h2 {
  font-size: 25px;
}

.btn {
  padding: 1rem 2rem;
  font-family: var(--font-base);
  background-color: var(--brand);
  color: #fff;
  border: 0;
  font-size: 1rem;
  font-weight: 300;
  transition: color 0.3s ease;
  cursor: pointer;
}
.btn:hover {
  color: rgba(255, 255, 255, 0.7);
}

.page {
  max-width: 1000px;
  margin: 0 auto;
  padding: 0 1rem;
}

.page__header {
  display: flex;
  justify-content: center;
  position: sticky;
  background-color: rgba(255, 255, 255, 0.9);
  top: 0;
  z-index: 1;
}

.logo {
  max-width: 100px;
  display: inline-flex;
}
.logo svg {
  width: 100%;
  height: 50px;
}

input[type=checkbox] {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 1;
  width: 100%;
  height: 100%;
  cursor: pointer;
  opacity: 0;
}

.checkbox {
  background-color: #e9e9e9;
  width: 22px;
  height: 22px;
  display: block;
  position: absolute;
  border-radius: 50%;
  top: 0.7rem;
  right: 0.7rem;
  opacity: 0;
  transition: opacity 0.3s ease;
}
.checkbox:after {
  content: " ";
  position: absolute;
  top: 50%;
  left: 28%;
  display: table;
  width: 5.71428571px;
  height: 9.14285714px;
  border: 2px solid #fff;
  transform: rotate(45deg) scale(0) translate(-50%, -50%);
  opacity: 1;
  transition: all 0.1s cubic-bezier(0.71, -0.46, 0.88, 0.6), opacity 0.1s;
}

.users {
  display: grid;
  grid-template-columns: var(--users-cols, repeat(auto-fill, minmax(270px, 1fr)));
  grid-gap: var(--users-gap, 3rem 1.5rem);
}
@media (max-width: 600px) {
  .users {
    --cols: repeat(auto-fit,minmax(200px,1fr));
    --users-gap: 2rem 1rem;
  }
}

.user {
  position: relative;
}
.user img {
  max-width: 100%;
}
.user__image {
  position: relative;
}
.user__desc {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  opacity: 0;
  display: flex;
  align-items: center;
  padding: 2rem;
  background-color: rgba(255, 255, 255, 0.9);
  transition: opacity 0.3s ease;
}
.user__desc__text {
  transform: translateY(10px);
  transition: transform 0.3s ease-in;
  opacity: 0;
}
.user:hover .user__desc {
  cursor: pointer;
  opacity: 1;
}
.user:hover .user__desc__text {
  transform: translateY(0);
  opacity: 1;
}
.user:hover .user__data {
  cursor: pointer;
  transition: color 0.3s ease;
  color: var(--brand);
}
.user:hover .checkbox {
  opacity: 1;
}
.user__name {
  font-size: 21px;
  margin: 10px 0;
}
.user p {
  margin: 0;
}

input[type=checkbox]:checked + .user__info .checkbox {
  background-color: var(--brand);
  opacity: 1;
}
input[type=checkbox]:checked + .user__info .checkbox:after {
  border-top: 0;
  border-left: 0;
  transform: rotate(45deg) scale(1) translate(-50%, -50%);
  opacity: 1;
  transition: all 0.2s cubic-bezier(0.12, 0.4, 0.29, 1.46) 0.1s;
}

.form__bottom {
  display: flex;
  justify-content: center;
  margin: 2rem 0;
}

.content__header {
  margin-bottom: 4rem;
}
.content__header__info {
  display: grid;
}
@media (min-width: 601px) {
  .content__header__info {
    grid-template-columns: 4fr 10fr;
  }
}
.content__header .small {
  text-transform: uppercase;
  font-weight: bold;
  font-size: 10px;
}
@media (max-width: 600px) {
  .content__header .small {
    display: none;
  }
}

.team {
  display: none;
}
.build__team .team {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(255, 255, 255, 0.9);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
  z-index: 1;
}
.team__url {
  background-color: rgba(0, 0, 0, 0.8);
  color: #fff;
  padding: 1rem 2rem;
}
.team__close {
  position: absolute;
  right: 2rem;
  top: 2rem;
  cursor: pointer;
}
.team__close .line {
  background-color: #000;
  left: 0px;
  position: absolute;
  width: 18px;
  height: 2px;
  top: 0px;
}
.team__close .top {
  transform: rotate(135deg);
}
.team__close .bottom {
  transform: rotate(45deg);
}

.build__team {
  overflow: hidden;
}

.with-team .content__header__info,
.with-team .checkbox {
  display: none;
}

/* Page Loader */
.js .loading:before, .js .loading:after {
  content: "";
  position: fixed;
  z-index: 1100;
}

.js .loading::before {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: var(--color-bg);
}

.js .loading::after {
  top: 50%;
  left: 50%;
  width: 60px;
  height: 60px;
  margin: -30px 0 0 -30px;
  border-radius: 50%;
  opacity: 0.4;
  background: var(--brand);
  animation: loaderAnim 0.7s linear infinite alternate forwards;
}

@keyframes loaderAnim {
  to {
    opacity: 1;
    transform: scale3d(0.5, 0.5, 1);
  }
}
